import React, { Component } from 'react';
import './index.css';

class Profile extends Component {
  state = {
    name: 'John Doe',
    email: 'john.doe@example.com',
    bio: 'Software Developer with 5 years of experience',
    skills: 'React, JavaScript, CSS',
    picture: 'https://via.placeholder.com/150',
    isEditing: false,
    pictureFile: null
  };

  componentDidMount() {
    // Load saved profile data from local storage when component mounts
    const savedProfile = localStorage.getItem('profile');
    if (savedProfile) {
      const parsedProfile = JSON.parse(savedProfile);
      this.setState(parsedProfile);
    }
  }

  componentDidUpdate(prevProps, prevState) {
    // Save profile data to local storage whenever it changes
    if (prevState !== this.state && !this.state.isEditing) {
      const { isEditing, pictureFile, ...profileData } = this.state;
      localStorage.setItem('profile', JSON.stringify(profileData));
    }
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        this.setState({
          picture: reader.result,
          pictureFile: file
        });
      };
      reader.readAsDataURL(file);
    }
  };

  handleSubmit = (event) => {
    event.preventDefault();
    this.setState({ isEditing: false });
    console.log('Profile updated:', this.state);
  };

  toggleEdit = () => {
    this.setState(prevState => ({
      isEditing: !prevState.isEditing
    }));
  };

  render() {
    const { name, email, bio, skills, picture, isEditing } = this.state;

    return (
      <div className="profile-container">
        <h1>Profile Page</h1>
        
        <div className="profile-picture">
          <img src={picture} alt="Profile" />
          {isEditing && (
            <div className="image-upload">
              <input
                type="file"
                id="profile-upload"
                accept="image/*"
                onChange={this.handleImageChange}
                style={{ display: 'none' }}
              />
              <label htmlFor="profile-upload" className="upload-button">
                Choose Image
              </label>
              {picture !== 'https://via.placeholder.com/150' && (
                <button 
                  type="button" 
                  className="remove-button"
                  onClick={() => this.setState({ picture: 'https://via.placeholder.com/150', pictureFile: null })}
                >
                  Remove
                </button>
              )}
            </div>
          )}
        </div>

        {isEditing ? (
          <form onSubmit={this.handleSubmit} className="profile-form">
            <div className="form-group">
              <label>Name:</label>
              <input
                type="text"
                name="name"
                value={name}
                onChange={this.handleInputChange}
              />
            </div>

            <div className="form-group">
              <label>Email:</label>
              <input
                type="email"
                name="email"
                value={email}
                onChange={this.handleInputChange}
              />
            </div>

            <div className="form-group">
              <label>Bio:</label>
              <textarea
                name="bio"
                value={bio}
                onChange={this.handleInputChange}
              />
            </div>

            <div className="form-group">
              <label>Skills:</label>
              <input
                type="text"
                name="skills"
                value={skills}
                onChange={this.handleInputChange}
              />
            </div>

            <div className="form-buttons">
              <button type="submit">Save</button>
              <button type="button" onClick={this.toggleEdit}>Cancel</button>
            </div>
          </form>
        ) : (
          <div className="profile-display">
            <p><strong>Name:</strong> {name}</p>
            <p><strong>Email:</strong> {email}</p>
            <p><strong>Bio:</strong> {bio}</p>
            <p><strong>Skills:</strong> {skills}</p>
            <button onClick={this.toggleEdit}>Edit Profile</button>
          </div>
        )}
      </div>
    );
  }
}

export default Profile;
