import React, { Component } from 'react';

import Profile from './components/Profile';
import PostJob from './components/PostJob';
import Jobs from './components/Jobs';
import Home from './components/Home';

class App extends Component {
  state = {
    currentPage: window.location.pathname.slice(1) || '' // Default to empty string for home
  };

  componentDidMount() {
    // Listen for browser back/forward button navigation
    window.onpopstate = () => {
      this.setState({
        currentPage: window.location.pathname.slice(1) || ''
      });
    };
  }

  renderPage() {
    switch(this.state.currentPage) {
      case '':
        return <Home />;
      case 'profile':
        return <Profile />;
      case 'post-job':
        return <PostJob />;
      case 'jobs':
        return <Jobs />;
      default:
        return <Home />;
    }
  }

  render() {
    return (
      <div className="App">
        <Navbar />
        {this.renderPage()}
      </div>
    );
  }
}

export default App;
