import React, { Component } from 'react';
import './index.css';

class Jobs extends Component {
  state = {
    searchTerm: '',
    locationFilter: '',
    sortBy: 'newest'
  };

  handleSearch = (e) => {
    this.setState({ searchTerm: e.target.value });
  };

  handleLocationFilter = (e) => {
    this.setState({ locationFilter: e.target.value });
  };

  handleSortChange = (e) => {
    this.setState({ sortBy: e.target.value });
  };

  getFilteredJobs = () => {
    const { allJobs = [] } = this.props;
    // const { allJobs } = this.props;
    const { searchTerm, locationFilter, sortBy } = this.state;

    
    let filteredJobs = allJobs.filter(job => {
      const matchesSearch = job.title.toLowerCase().includes(searchTerm.toLowerCase()) || 
                          job.company.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesLocation = locationFilter === '' || 
                            job.location.toLowerCase().includes(locationFilter.toLowerCase());
      return matchesSearch && matchesLocation;
    });

    
    if (sortBy === 'newest') {
      filteredJobs.sort((a, b) => new Date(b.datePosted || 0) - new Date(a.datePosted || 0));
    } else if (sortBy === 'salary-high') {
      filteredJobs.sort((a, b) => {
        const getSalary = (salary) => parseInt(salary.replace(/[^0-9]/g, ''), 10) || 0;
        return getSalary(b.salary) - getSalary(a.salary);
      });
    }

    return filteredJobs;
  };

  render() {
    const { allJobs = [] } = this.props; // Ensure allJobs is an array
    const { searchTerm, locationFilter } = this.state;
  
    // If no filters are applied, show all jobs
    const showAllJobs = searchTerm === '' && locationFilter === '';
  
    // Get filtered jobs if filters are applied
    const filteredJobs = this.getFilteredJobs();
  
    // Decide which jobs to display
    const jobsToDisplay = showAllJobs ? allJobs : filteredJobs;
  
    return (
      <div className="jobs-container">
        <h1 className="jobs-header">Available Job Listings</h1>
  
        <div className="jobs-controls">
          <div className="search-filter">
            <input
              type="text"
              placeholder="üîç Search jobs or companies"
              onChange={this.handleSearch}
              className="search-input"
            />
            <input
              type="text"
              placeholder="üìç Filter by location"
              onChange={this.handleLocationFilter}
              className="location-input"
            />
          </div>
  
          <div className="sort-options">
            <label>Sort by:</label>
            <select onChange={this.handleSortChange} value={this.state.sortBy}>
              <option value="newest">Newest First</option>
              <option value="salary-high">Highest Salary</option>
            </select>
          </div>
        </div>
  
        <div className="jobs-list">
          {jobsToDisplay.length > 0 ? (
            jobsToDisplay.map(job => (
              <div key={job.id} className="job-card">
                <div className="job-header">
                  <h2 className="job-title">{job.title}</h2>
                  <span className="job-salary">{job.salary}</span>
                </div>
                <h3 className="job-company">{job.company}</h3>
                <div className="job-meta">
                  <span className="job-location">üìç {job.location}</span>
                  {job.datePosted && (
                    <span className="job-posted">
                      üóì {new Date(job.datePosted).toLocaleDateString()}
                    </span>
                  )}
                </div>
                <p className="job-description">{job.description}</p>
                <button className="apply-button">Apply Now</button>
              </div>
            ))
          ) : (
            <div className="no-jobs">
              <p>No jobs found matching your criteria.</p>
              <p>Try adjusting your search filters.</p>
            </div>
          )}
        </div>
      </div>
    );
  }
  
}

export default Jobs;
