import { useState } from "react";

import { Link} from "react-router-dom";
import { FiMenu, FiX } from "react-icons/fi";
import './index.css'

const Navbar = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const onClickLogout = () => {
    Cookies.remove("jwt_token")
    const {history} = props
    history.replace('/login')
  }

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav className="navbar">
      <div className="logo-section">
        <img 
          src="https://thumbs.dreamstime.com/b/vector-logo-fruits-vegetables-round-sign-organic-healthy-vegan-food-circle-badge-price-tag-fruit-store-label-101863462.jpg" 
          alt="Logo" 
          className="logo" 
        />
      </div>
      <div className="menu-icon" onClick={toggleMenu}>
        {isOpen ? <FiX /> : <FiMenu />}
      </div>
      <ul className={`nav-links ${isOpen ? "open" : ""}`}>
        <li><Link to='/login'>Login</Link></li>
        <li><Link to="/signup">Signup</Link></li>
        <li><Link to='/profile'>Profile</Link></li>
        <li><Link to="/postjobs">Post Jobs</Link></li>
        <li><Link to="/jobs">Jobs</Link></li>
        <li>
          <img 
            src="https://www.shareicon.net/data/512x512/2016/05/24/770137_man_512x512.png" 
            alt="profile" 
            className="userprofile"
          />
        </li>
        <div className="login-system-section">           
          <button className="logout-button" onClick={onClickLogout}>Logout</button>
        </div>
      </ul>
    </nav>
  );
};

export default Navbar;
