import React, { Component } from 'react';
import './index.css';

class PostJob extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      company: '',
      location: '',
      salary: '',
      description: '',
    };
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const {title,company,location,salary,description} = this.state

    const url = "http://localhost:8000/post-jobs"
    const options = {
      method : "POST",
      headers : { "Content-Type": "application/json" },
      body: JSON.stringify({title,company,location,salary,description}),
    }

    const response = await fetch(url,options)
    const data = await response.json()
    console.log(data)
    
  };

  render() {
    const { title, company, location, salary, description } = this.state;

    return (
      <div className="post-job-container">
        <h2 className="form-title">Post a New Job</h2>
        <form onSubmit={this.handleSubmit} className="job-form">
          <input
            type="text"
            name="title"
            value={title}
            onChange={this.handleChange}
            placeholder="Job Title"
            className="form-input"
            required
          />
          <input
            type="text"
            name="company"
            value={company}
            onChange={this.handleChange}
            placeholder="Company Name"
            className="form-input"
            required
          />
          <input
            type="text"
            name="location"
            value={location}
            onChange={this.handleChange}
            placeholder="Location"
            className="form-input"
            required
          />
          <input
            type="text"
            name="salary"
            value={salary}
            onChange={this.handleChange}
            placeholder="Salary (e.g., $50,000 - $60,000)"
            className="form-input"
          />
          <textarea
            name="description"
            value={description}
            onChange={this.handleChange}
            placeholder="Job Description"
            className="form-textarea"
            required
          />
          <button type="submit" className="submit-button">
            Post Job
          </button>
        </form>
      </div>
    );
  }
}

export default PostJob;
