import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './in.css';

class Signup extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      email: '',
      password: '',
      confirmPassword: '',
      userType: 'candidate',
      termsAgreed: false,
      errors: {}
    };
  }

  handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    this.setState({
      [name]: type === 'checkbox' ? checked : value,
      errors: {
        ...this.state.errors,
        [name]: ''
      }
    });
  };

  validateForm = () => {
    const { firstName, lastName, email, password, confirmPassword, termsAgreed } = this.state;
    const errors = {};
    let isValid = true;

    if (!firstName) {
      errors.firstName = 'First name is required';
      isValid = false;
    }

    if (!lastName) {
      errors.lastName = 'Last name is required';
      isValid = false;
    }

    if (!email) {
      errors.email = 'Email is required';
      isValid = false;
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      errors.email = 'Email is invalid';
      isValid = false;
    }

    if (!password) {
      errors.password = 'Password is required';
      isValid = false;
    } else if (password.length < 8) {
      errors.password = 'Password must be at least 8 characters';
      isValid = false;
    }

    if (password !== confirmPassword) {
      errors.confirmPassword = 'Passwords do not match';
      isValid = false;
    }

    if (!termsAgreed) {
      errors.termsAgreed = 'You must agree to the terms';
      isValid = false;
    }

    this.setState({ errors });
    return isValid;
  };

  handleSubmit = (e) => {
    e.preventDefault();
    if (this.validateForm()) {
      console.log('Signup form submitted:', this.state);
      // Add your registration logic here
    }
  };

  render() {
    const { firstName, lastName, email, password, confirmPassword, userType, termsAgreed, errors } = this.state;

    return (
      <div className="auth-container">
        <div className="auth-card">
          <div className="auth-header">
            <h2>Create Your Account</h2>
            <p>Join thousands of professionals finding their dream jobs</p>
          </div>

          <form onSubmit={this.handleSubmit} className="auth-form">
            <div className="name-fields">
              <div className={`form-group half-width ${errors.firstName ? 'error' : ''}`}>
                <label htmlFor="firstName">First Name</label>
                <input
                  type="text"
                  id="firstName"
                  name="firstName"
                  placeholder="John"
                  value={firstName}
                  onChange={this.handleChange}
                />
                {errors.firstName && <span className="error-message">{errors.firstName}</span>}
              </div>
              <div className={`form-group half-width ${errors.lastName ? 'error' : ''}`}>
                <label htmlFor="lastName">Last Name</label>
                <input
                  type="text"
                  id="lastName"
                  name="lastName"
                  placeholder="Doe"
                  value={lastName}
                  onChange={this.handleChange}
                />
                {errors.lastName && <span className="error-message">{errors.lastName}</span>}
              </div>
            </div>

            <div className={`form-group ${errors.email ? 'error' : ''}`}>
              <label htmlFor="email">Email Address</label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="you@example.com"
                value={email}
                onChange={this.handleChange}
              />
              {errors.email && <span className="error-message">{errors.email}</span>}
            </div>

            <div className={`form-group ${errors.password ? 'error' : ''}`}>
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                name="password"
                placeholder="••••••••"
                value={password}
                onChange={this.handleChange}
              />
              {errors.password && <span className="error-message">{errors.password}</span>}
              <div className="password-hint">Minimum 8 characters</div>
            </div>

            <div className={`form-group ${errors.confirmPassword ? 'error' : ''}`}>
              <label htmlFor="confirmPassword">Confirm Password</label>
              <input
                type="password"
                id="confirmPassword"
                name="confirmPassword"
                placeholder="••••••••"
                value={confirmPassword}
                onChange={this.handleChange}
              />
              {errors.confirmPassword && <span className="error-message">{errors.confirmPassword}</span>}
            </div>

            <div className="form-group">
              <label>I am a:</label>
              <div className="radio-group">
                <label>
                  <input
                    type="radio"
                    name="userType"
                    value="candidate"
                    checked={userType === 'candidate'}
                    onChange={this.handleChange}
                  />
                  Job Seeker
                </label>
                <label>
                  <input
                    type="radio"
                    name="userType"
                    value="employer"
                    checked={userType === 'employer'}
                    onChange={this.handleChange}
                  />
                  Employer
                </label>
              </div>
            </div>

            <div className={`form-group checkbox-group ${errors.termsAgreed ? 'error' : ''}`}>
              <input
                type="checkbox"
                id="termsAgreed"
                name="termsAgreed"
                checked={termsAgreed}
                onChange={this.handleChange}
              />
              <label htmlFor="termsAgreed">
                I agree to the <Link to="/terms">Terms of Service</Link> and <Link to="/privacy">Privacy Policy</Link>
              </label>
              {errors.termsAgreed && <span className="error-message">{errors.termsAgreed}</span>}
            </div>

            <button type="submit" className="auth-btn primary-btn">Create Account</button>
          </form>

          <div className="auth-footer">
            <p>Already have an account? <Link to="/login" className="auth-link">Sign in</Link></p>
          </div>
        </div>
      </div>
    );
  }
}

export default Signup;
