import React, { Component } from 'react';
import './index.css';

class PostJob extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      company: '',
      location: '',
      salary: '',
      description: '',
      error: null,
      success: false
    };
  }

  handleChange = (e) => {
    const { name, value } = e.target;
    // For salary field, only allow numbers
    if (name === 'salary') {
      if (value === '' || /^\d+$/.test(value)) {
        this.setState({ 
          [name]: value,
          error: null
        });
      }
    } else {
      this.setState({ 
        [name]: value,
        error: null
      });
    }
  };

  handleSubmit = async (e) => {
    e.preventDefault();
    const { title, company, location, salary, description } = this.state;
  
    // Basic validation
    if (!title || !company || !location || !description) {
      this.setState({ error: "Please fill in all required fields" });
      return;
    }

    try {
      const response = await fetch("http://localhost:8000/post-jobs", {
        method: "POST",
        headers: { 
          "Content-Type": "application/json" 
        },
        body: JSON.stringify({
          title,
          company,
          location,
          salary: salary ? parseInt(salary) : null,
          description
        }),
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to post job");
      }

      const data = await response.json();
      console.log("Success:", data);
      
      // Clear form and show success message
      this.setState({
        title: '',
        company: '',
        location: '',
        salary: '',
        description: '',
        success: true,
        error: null
      });

      // Hide success message after 3 seconds
      setTimeout(() => this.setState({ success: false }), 3000);
      
    } catch (error) {
      console.error("Error submitting job:", error);
      this.setState({ 
        error: error.message || "Failed to submit job posting",
        success: false
      });
    }
  };

  render() {
    const { title, company, location, salary, description, error, success } = this.state;

    return (
      <div className="post-job-container">
        <h2 className="form-title">Post a New Job</h2>
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">Job posted successfully!</div>}
        <form onSubmit={this.handleSubmit} className="job-form">
          <input
            type="text"
            name="title"
            value={title}
            onChange={this.handleChange}
            placeholder="Job Title"
            className="form-input"
            required
          />
          <input
            type="text"
            name="company"
            value={company}
            onChange={this.handleChange}
            placeholder="Company Name"
            className="form-input"
            required
          />
          <input
            type="text"
            name="location"
            value={location}
            onChange={this.handleChange}
            placeholder="Location"
            className="form-input"
            required
          />
          <input
            type="text"
            name="salary"
            value={salary}
            onChange={this.handleChange}
            placeholder="Salary (e.g., 850000)"
            className="form-input"
            pattern="\d*" // Ensures only numbers can be entered
          />
          <textarea
            name="description"
            value={description}
            onChange={this.handleChange}
            placeholder="Job Description"
            className="form-textarea"
            required
          />
          <button type="submit" className="submit-button">
            Post Job
          </button>
        </form>
      </div>
    );
  }
}

export default PostJob;
