import React, { Component } from 'react';
import './index.css';

class Navbar extends Component {
  state = {
    activeItem: '' // Default to empty string for home
  };

  handleItemClick = (item) => {
    this.setState({ activeItem: item });
    switch(item) {
      case '':
        window.location.href = '/';
        break;
      case 'profile':
        window.location.href = '/profile';
        break;
      case 'post-job':
        window.location.href = '/post-job';
        break;
      case 'jobs':
        window.location.href = '/jobs';
        break;
      default:
        break;
    }
  };

  render() {
    const { activeItem } = this.state;

    return (
      <nav className="navbar">
        <div className="navbar-brand">
          <h2 onClick={() => this.handleItemClick('')}>Job Portal</h2>
        </div>
        <ul className="navbar-menu">
          <li 
            className={`navbar-item ${activeItem === 'profile' ? 'active' : ''}`}
            onClick={() => this.handleItemClick('profile')}
          >
            Profile
          </li>
          <li 
            className={`navbar-item ${activeItem === 'post-job' ? 'active' : ''}`}
            onClick={() => this.handleItemClick('post-job')}
          >
            Post Job
          </li>
          <li 
            className={`navbar-item ${activeItem === 'jobs' ? 'active' : ''}`}
            onClick={() => this.handleItemClick('jobs')}
          >
            Jobs
          </li>
        </ul>
      </nav>
    );
  }
}

export default Navbar;
