import { useState } from "react";
import { Link } from "react-router-dom";
import { Menu, X } from "lucide-react";
import { Button } from "@/components/ui/button";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="bg-white shadow-md w-full">
      <div className="container mx-auto flex justify-between items-center p-4">
        <Link to="/" className="text-2xl font-bold text-blue-600">
          JobsHub
        </Link>

        <div className="hidden md:flex space-x-6">
          <Link to="/jobs" className="text-gray-700 hover:text-blue-600">Jobs</Link>
          <Link to="/companies" className="text-gray-700 hover:text-blue-600">Companies</Link>
          <Link to="/about" className="text-gray-700 hover:text-blue-600">About</Link>
          <Link to="/contact" className="text-gray-700 hover:text-blue-600">Contact</Link>
        </div>

        <div className="hidden md:flex">
          <Button variant="outline">Login</Button>
        </div>

        {/* Mobile Menu Button */}
        <button className="md:hidden" onClick={() => setIsOpen(!isOpen)}>
          {isOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Mobile Menu */}
      {isOpen && (
        <div className="md:hidden bg-white shadow-md p-4 flex flex-col space-y-4">
          <Link to="/jobs" className="text-gray-700 hover:text-blue-600">Jobs</Link>
          <Link to="/companies" className="text-gray-700 hover:text-blue-600">Companies</Link>
          <Link to="/about" className="text-gray-700 hover:text-blue-600">About</Link>
          <Link to="/contact" className="text-gray-700 hover:text-blue-600">Contact</Link>
          <Button variant="outline" className="w-full">Login</Button>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
