import React from 'react';
import './index.css';

const Jobs = ({ allJobs }) => {
  if (!allJobs) {
    return <p>Error: Job data is missing!</p>;
  }

  // Destructure only if allJobs exists
  const { title, company, salary, location, description } = allJobs;

  return (
    <div className="job-card">
      <div className="job-header">
        <h2 className="job-title">{title}</h2>
        <span className="job-salary">{salary}</span>
      </div>
      <h3 className="job-company">{company}</h3>
      <div className="job-meta">
        <span className="job-location">üìç {location}</span>
      </div>
      <p className="job-description">{description}</p>
      <button className="apply-button">Apply Now</button>
    </div>
  );
};

export default Jobs;
