import React from 'react';
import './.css';

class JobPostingForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      company: '',
      location: '',
      salary: '',
      description: ''
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();
    console.log('Job posted:', this.state);
    // Submit logic would go here
  };

  render() {
    return (
      <div className="job-posting-container">
        <h2>Post a New Job</h2>
        <form onSubmit={this.handleSubmit} className="job-posting-form">
          <div className="form-group">
            <label htmlFor="title">Job Title*</label>
            <input
              type="text"
              id="title"
              name="title"
              value={this.state.title}
              onChange={this.handleChange}
              placeholder="e.g. Senior Software Engineer"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="company">Company Name*</label>
            <input
              type="text"
              id="company"
              name="company"
              value={this.state.company}
              onChange={this.handleChange}
              placeholder="Your company name"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="location">Location*</label>
            <input
              type="text"
              id="location"
              name="location"
              value={this.state.location}
              onChange={this.handleChange}
              placeholder="e.g. New York, NY or Remote"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="salary">Salary Range</label>
            <input
              type="text"
              id="salary"
              name="salary"
              value={this.state.salary}
              onChange={this.handleChange}
              placeholder="e.g. $90,000 - $120,000 per year"
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Job Description*</label>
            <textarea
              id="description"
              name="description"
              value={this.state.description}
              onChange={this.handleChange}
              placeholder="Detailed description of the role and responsibilities"
              rows="6"
              required
            />
          </div>

          <button type="submit" className="submit-btn">
            Post Job
          </button>
        </form>
      </div>
    );
  }
}

export default JobPostingForm;
