import React, { Component } from 'react';
import "./index.css"

const initialjobsList = [
  {
    id: 1,
    title: "Frontend Developer",
    company: "TechCorp Inc.",
    location: "San Francisco, CA (Remote)",
    salary: "$90,000 - $120,000",
    description: "We're looking for a skilled React developer to join our team. You'll work on building responsive user interfaces for our SaaS platform."
  },
  {
    id: 2,
    title: "UX Designer",
    company: "Creative Solutions",
    location: "New York, NY",
    salary: "$80,000 - $110,000",
    description: "Join our design team to create beautiful, intuitive user experiences for our clients. Portfolio required."
  },
  {
    id: 3,
    title: "Data Scientist",
    company: "Analytics Pro",
    location: "Boston, MA (Hybrid)",
    salary: "$110,000 - $150,000",
    description: "Seeking data scientist with Python and machine learning experience to analyze large datasets and build predictive models."
  }
]

class PostJob extends Component {
  state = {title : "" , company : "" , salary : "" , location : "", description : "" , jobList : initialjobsList}

  onSubmitForm = (event) =>{
    event.preventDefault()
    const {title,location,salary,description,company,jobList} = this.state

    const newJob = {
      title,
      company,
      salary,
      location,
      description
    }




  }

  onChangetitle = (event) =>{
    this.setState({title : event.target.value})
  }

  onChangecomapny = (event) =>{
    this.setState({company : event.target.value})
  }

  onChangelocation = (event) =>{
    this.setState({location : event.target.value})
  }

  onChangesalary = (event) =>{
    this.setState({salary : event.target.value})
  }

  onChangedescription = (event) =>{
    this.setState({description : event.target.value})
  }



  render() {
    const {title,comapny,salary,location,description} = this.state
    return (
      <div className="post-job-container">
        <form className='postjob-fromcontainer' onSubmit={this.onSubmitForm}>
          <h1 className='postjob-heading'>Post Job</h1>
          <div className='input-container'>
            <input type="text" placeholder='Job Title' className='input-field' value={title} onChange={this.onChangetitle}/>
          </div>
          <div className='input-container'>
            <input type="text" placeholder='Company' className='input-field' value={comapny} onChange={this.onChangecomapny}/>
          </div>
          <div className='input-container'>
            <input type="text" placeholder='Location' className='input-field' value={location} onChange={this.onChangelocation}/>
          </div>
          <div className='input-container'>
            <input type="text" placeholder='Salary' className='input-field' value={salary} onChange={this.onChangesalary}/>
          </div>
          <div className='input-container'>
            <input type="text" placeholder='Description' className='input-field' value={description} onChange={this.onChangedescription}/>
          </div>
          <div className='button-container'>
            <button className='post-button'>Post</button>
          </div>
        </form>
      </div>
    );
  }
}

export default PostJob;
