import React, { Component } from 'react';
import "./index.css"

class PostJob extends Component {
  state = {title : "" , comapny : "" , salary : "" , location : "", description : ""}

  onSubmitForm = (event) =>{
    event.preventDefault()
    const {title,location,salary,description,comapny} = this.st
  }

  onChangetitle = (event) =>{
    this.setState({title : event.target.value})
  }

  onChangecomapny = (event) =>{
    this.setState({comapny : event.target.value})
  }

  onChangelocation = (event) =>{
    this.setState({location : event.target.value})
  }

  onChangesalary = (event) =>{
    this.setState({salary : event.target.value})
  }

  onChangedescription = (event) =>{
    this.setState({description : event.target.value})
  }



  render() {
    const {title,comapny,salary,location,description} = this.state
    return (
      <div className="post-job-container">
        <form className='postjob-fromcontainer' onSubmit={this.onSubmitForm}>
          <h1 className='postjob-heading'>Post Job</h1>
          <div className='input-container'>
            <input type="text" placeholder='Job Title' className='input-field' value={title} onChange={this.onChangetitle}/>
          </div>
          <div className='input-container'>
            <input type="text" placeholder='Company' className='input-field' value={comapny} onChange={this.onChangecomapny}/>
          </div>
          <div className='input-container'>
            <input type="text" placeholder='Location' className='input-field' value={location} onChange={this.onChangelocation}/>
          </div>
          <div className='input-container'>
            <input type="text" placeholder='Salary' className='input-field' value={salary} onChange={this.onChangesalary}/>
          </div>
          <div className='input-container'>
            <input type="text" placeholder='Description' className='input-field' value={description} onChange={this.onChangedescription}/>
          </div>
          <div className='button-container'>
            <button className='post-button'>Post</button>
          </div>
        </form>
      </div>
    );
  }
}

export default PostJob;
