import React, { Component } from 'react';
import Jobs from '../Jobs';
import { v4 as uuidv4 } from 'uuid';
import './index.css';

const initialjobsList = [
  {
    id: uuidv4(),
    title: 'Frontend Developer',
    company: 'TechCorp Inc.',
    location: 'San Francisco, CA (Remote)',
    salary: '$90,000 - $120,000',
    description: "We're looking for a skilled React developer to join our team. You'll work on building responsive user interfaces for our SaaS platform.",
  },
  {
    id: uuidv4(),
    title: 'UX Designer',
    company: 'Creative Solutions',
    location: 'New York, NY',
    salary: '$80,000 - $110,000',
    description: 'Join our design team to create beautiful, intuitive user experiences for our clients. Portfolio required.',
  },
  {
    id: uuidv4(),
    title: 'Data Scientist',
    company: 'Analytics Pro',
    location: 'Boston, MA (Hybrid)',
    salary: '$110,000 - $150,000',
    description: 'Seeking data scientist with Python and machine learning experience to analyze large datasets and build predictive models.',
  },
];

class PostJob extends Component {
  state = {
    title: '',
    company: '',
    salary: '',
    location: '',
    description: '',
    jobList: initialjobsList,
  };

  onSubmitForm = (event) => {
    event.preventDefault();
    const { title, location, salary, description, company } = this.state;

    if (title && company && salary && location && description) {
      const newJob = {
        id: uuidv4(),
        title,
        company,
        salary,
        location,
        description,
      };

      this.setState((prevState) => ({
        jobList: [...prevState.jobList, newJob],
        title: '', // Reset fields
        company: '',
        salary: '',
        location: '',
        description: '',
      }));
    } else {
      alert('Please fill in all fields'); 
    }
  };

  handleChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  render() {
    const { title, company, salary, location, description, jobList } = this.state;

    return (
      <>
        <div className="post-job-container">
          <form className="postjob-formcontainer" onSubmit={this.onSubmitForm}>
            <h1 className="postjob-heading">Post Job</h1>
            <div className="input-container">
              <input
                type="text"
                name="title"
                placeholder="Job Title"
                className="input-field"
                value={title}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-container">
              <input
                type="text"
                name="company"
                placeholder="Company"
                className="input-field"
                value={company}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-container">
              <input
                type="text"
                name="location"
                placeholder="Location"
                className="input-field"
                value={location}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-container">
              <input
                type="text"
                name="salary"
                placeholder="Salary"
                className="input-field"
                value={salary}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-container">
              <input
                type="text"
                name="description"
                placeholder="Description"
                className="input-field"
                value={description}
                onChange={this.handleChange}
              />
            </div>
            <div className="button-container">
              <button type="submit" className="post-button">
                Post
              </button>
            </div>
          </form>
        </div>

        <div className="jobs-section">
          {jobList.map((eachJob) => (
            <Jobs key={eachJob.id} JobDetails={eachJob} />
          ))}
        </div>
      </>
    );
  }
}

export default PostJob;
